import '../assets/CSS/background.css'
import {motion, animate, transform, stagger, frame, cubicBezier} from "framer-motion"
import BouncingBall from "../components/bouncingball"; 
import AnimatedTextCharacter from "../components/animatedcharecter";
import AnimatedTextWord from "../components/animatedwor";

function Background(){
    return(
<div className='html'>
<div id="stars"></div>
<div id="stars2"></div>
<div id="stars3"></div>
<div id="title">
  <span>
  <AnimatedTextWord text='Mackay Fisher'/>
  </span>
  <motion.div className="animatedline"
                        animate={{scale: 1, width: '80%', height: 3}}
                        initial={{scale : 0}}
                        transition={{ ease: 'linear', duration: 2.5, delay: 1.3}}>
                        </motion.div>
  <span>
  <AnimatedTextCharacter text="Software Developer"/>
  </span>
</div>
</div>
    )
}

export default Background
