import React from "react";
import Navbar from "../components/navbar";
import '../assets/CSS/home.css'
import brain1 from '../assets/images/brain2.png'
import {motion, animate, transform, stagger, frame, cubicBezier} from "framer-motion"
import BouncingBall from "../components/bouncingball"; 
import AnimatedTextCharacter from "../components/animatedcharecter";
import AnimatedTextWord from "../components/animatedwor";
const words = props.text.split(" ");

  const container = {
    hidden: { opacity: 0 },
    visible: (i = 1) => ({
      opacity: 1,
      transition: { staggerChildren: 0.12, delayChildren: 0.04 * i },
    }),
  };

  const child = {
    visible: {
        delay:{
            stagger: props.stagger,
        },
      opacity: 1,
      x: 0,
      transition: {
        type: "spring",
        damping: 12,
        stiffness: 100,
      },
    },
    hidden: {
      opacity: 0,
      x: 20,
      transition: {
        type: "spring",
        damping: 12,
        stiffness: 100,
      },
    },
  };
function Home(){
    return(
        <div>
            <motion.div
            initial={{scale:1,width:'.1%'}}
            animate={{scale:1, width:'100%'}}
            transition={{duration:1}}>
            <Navbar/>
            </motion.div>

            <div className="under-nav">
            <motion.div className="animation-hold"
            animate={{scale: 1, width: '40%', height: 3, y:100, rotate: 180}}
            initial={{scale : .25, width: 20, height: 20,borderRadius:20}}
            transition={{ ease: 'linear', duration: 3, y: {duration: 3}, rotate:{duration:3}}}>
                 <motion.div
                    style={{ overflow: "hidden", display: "flex", fontSize: "2rem" }}
                    variants={container}
                    initial="hidden"
                    animate="visible"
                    >
                    {words.map((word, index) => (
                        <motion.span
                        variants={child}
                        style={{ marginRight: "5px" }}
                        key={index}
                        >
                        {word}
                        </motion.span>
                    ))}
                    </motion.div>
            </motion.div>
            </div>
            <div className="under-nav2">
                <div className="brain-hold">
                    <img src={brain1} className='braina' alt='braina'/>
                    <img src={brain1} className='brainb' alt='brainb'/>
                    <img src={brain1} className='brainc' alt='brainc'/>
                    <img src={brain1} className='braind' alt='braind'/>
                </div>
            </div>
        </div>
    )
}

export default Home

